  /* @jsxRuntime classic */
/* @jsx mdx */
import useBaseUrl from '@docusaurus/useBaseUrl';
export const toc = [{
  value: 'Simulating payment transactions',
  id: 'simulating-payment-transactions',
  children: [{
    value: 'Account balance',
    id: 'account-balance',
    children: []
  }, {
    value: 'Make a transfer',
    id: 'make-a-transfer',
    children: []
  }, {
    value: 'Pay bill',
    id: 'pay-bill',
    children: []
  }]
}];

const layoutProps = {
  toc
};
const MDXLayout = "wrapper"
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">


    <h2 {...{
      "id": "simulating-payment-transactions"
    }}>{`Simulating payment transactions`}</h2>
    <div {...{
      "className": "admonition admonition-note alert alert--secondary"
    }}><div parentName="div" {...{
        "className": "admonition-heading"
      }}><h5 parentName="div"><span parentName="h5" {...{
            "className": "admonition-icon"
          }}><svg parentName="span" {...{
              "xmlns": "http://www.w3.org/2000/svg",
              "width": "14",
              "height": "16",
              "viewBox": "0 0 14 16"
            }}><path parentName="svg" {...{
                "fillRule": "evenodd",
                "d": "M6.3 5.69a.942.942 0 0 1-.28-.7c0-.28.09-.52.28-.7.19-.18.42-.28.7-.28.28 0 .52.09.7.28.18.19.28.42.28.7 0 .28-.09.52-.28.7a1 1 0 0 1-.7.3c-.28 0-.52-.11-.7-.3zM8 7.99c-.02-.25-.11-.48-.31-.69-.2-.19-.42-.3-.69-.31H6c-.27.02-.48.13-.69.31-.2.2-.3.44-.31.69h1v3c.02.27.11.5.31.69.2.2.42.31.69.31h1c.27 0 .48-.11.69-.31.2-.19.3-.42.31-.69H8V7.98v.01zM7 2.3c-3.14 0-5.7 2.54-5.7 5.68 0 3.14 2.56 5.7 5.7 5.7s5.7-2.55 5.7-5.7c0-3.15-2.56-5.69-5.7-5.69v.01zM7 .98c3.86 0 7 3.14 7 7s-3.14 7-7 7-7-3.12-7-7 3.14-7 7-7z"
              }}></path></svg></span>{`note`}</h5></div><div parentName="div" {...{
        "className": "admonition-content"
      }}><p parentName="div">{`Note: On registration, users will be automatically credited with $100. Any transactions will reduce the balance. When the balance reaches $0, the system will be auto-reloaded to $100.`}</p></div></div>
    <h3 {...{
      "id": "account-balance"
    }}>{`Account balance`}</h3>
    <ol>
      <li parentName="ol">{`You can check your account’s balance by using the `}<strong parentName="li">{`Account Balance`}</strong>{` option on the home page.`}</li>
      <li parentName="ol">{`Once selected, you will be requested to `}<strong parentName="li">{`authenticate your identity`}</strong>{` using any enrolled biometric option.`}</li>
      <li parentName="ol">{`Once successfully authenticated, your `}<strong parentName="li">{`account balance`}</strong>{` will be displayed on the screen`}</li>
    </ol>
    <div style={{
      textAlign: 'center'
    }}>
      <img alt="Balance Screen1" src={useBaseUrl('img/app-manual/balance1.png')} />
    </div>
    <h3 {...{
      "id": "make-a-transfer"
    }}>{`Make a transfer`}</h3>
    <ol>
      <li parentName="ol">{`You can make a transfer to a predefined account number (i.e 112233) from your account. `}</li>
      <li parentName="ol">{`Click the `}<strong parentName="li">{`Make a transfer`}</strong>{` option on the home page.`}</li>
      <li parentName="ol">{`Enter the `}<strong parentName="li">{`account number`}</strong>{` and the `}<strong parentName="li">{`amount`}</strong>{` you wish to transfer. Click on `}<strong parentName="li">{`Continue`}</strong>{` to proceed.`}</li>
      <li parentName="ol">{`If the account number is `}<strong parentName="li">{`valid`}</strong>{` (i.e. 112233) you will be requested to `}<strong parentName="li">{`authenticate your identity`}</strong>{` using any enrolled biometric option.`}</li>
    </ol>
    <div style={{
      textAlign: 'center'
    }}>
      <img alt="Transfer Screen1" src={useBaseUrl('img/app-manual/transfer1.png')} />
    </div>
    <ol {...{
      "start": 5
    }}>
      <li parentName="ol">{`On successful authentication, if the amount to be transferred is available in your account, the transaction would be successful and a success message will be displayed on the screen. `}</li>
    </ol>
    <div style={{
      textAlign: 'center'
    }}>
      <img alt="Transfer Screen2" src={useBaseUrl('img/app-manual/transfer2.png')} />
      <img alt="Transfer Screen3" src={useBaseUrl('img/app-manual/transfer3.png')} />
    </div>
    <ol {...{
      "start": 6
    }}>
      <li parentName="ol">{`If the amount transferred is more than the amount in your account, the transaction would fail and an error toast message would be displayed “Sorry, you do not have the required funds in your account to make this transaction”.`}</li>
    </ol>
    <div style={{
      textAlign: 'center'
    }}>
      <img alt="Transfer Screen2" src={useBaseUrl('img/app-manual/transfer4.png')} />
    </div>
    <h3 {...{
      "id": "pay-bill"
    }}>{`Pay bill`}</h3>
    <ol>
      <li parentName="ol">{`You can make a payment against a predefined bill number (i.e 12345 or 54321) from your account.`}</li>
      <li parentName="ol">{`Click the `}<strong parentName="li">{`Pay bill`}</strong>{` option on the home page.`}</li>
      <li parentName="ol">{`Enter the `}<strong parentName="li">{`bill number`}</strong>{` and click `}<strong parentName="li">{`Continue`}</strong>{` to proceed.`}</li>
      <li parentName="ol">{`If the `}<strong parentName="li">{`bill number`}</strong>{` is valid you will be requested to `}<strong parentName="li">{`authenticate your identity`}</strong>{` using any enrolled biometric option.`}</li>
      <li parentName="ol">{`On successful authentication, if the amount to be paid against the bill is `}<strong parentName="li">{`available in your account`}</strong>{`, the transaction will be successful.`}</li>
      <li parentName="ol">{`If the amount to be paid against the bill is `}<strong parentName="li">{`more than the amount available in your account`}</strong>{`, the transaction would fail and an `}<strong parentName="li">{`error message`}</strong>{` would be displayed;“Sorry, you do not have the required funds in your account to make this transaction”.`}</li>
    </ol>
    <div style={{
      textAlign: 'center'
    }}>
      <img alt="Bill Screen1" src={useBaseUrl('img/app-manual/bill1.png')} />
      <img alt="Bill Screen2" src={useBaseUrl('img/app-manual/bill2.png')} />
    </div>
    </MDXLayout>;
}

;
MDXContent.isMDXComponent = true;
